import React from 'react';
import Head from 'next/head';

import styled from 'styled-components';

import '../css/normalize.css';
import '../css/skeleton.css';
import '../css/mobile.css';
import '../css/scroller.css';
import '../css/sign-up.css';
import '../css/shoppingCart.css';

class SignUp extends React.Component{
	constructor(props) {
		super(props);

        this.state = {
           month:''
		};
		this.isNumberKey = this.isNumberKey.bind(this);
    }


	state={
		month:''
	}

	render(){
		console.log(this.state);
		return(
			<div className='signUpBox'>
				<form>
					<div id='signUp'>

					<h3 id='signUpHeading'>CREATE ACCOUNT</h3>				

					<label for='first_name' id='firstNameSignUpText' >First Name</label>
					<input type='text' name='first_name' id='firstNameSignup' />
					<label for='last_name' id='lastNameSignUpText' >Last Name</label>
					<input type='text' name='last_name' id='lastNameSignup' />
					<label for='email' id='emailSignUpText' >Email</label>
					<input type='text' name='email_field' id='emailSignUp' />
					<label for='dob' id ='birthdaySignUpText' >Birthday</label>s
					<input type='text' name='month' id='monthSignup' pattern='[0-9]*' minlength='2' maxlength='2'  autocomplete='off' onChange={this.isNumberKey} value={this.state.month}/>
					<input type='text' name='day' id='daySignup' pattern='[0-9]*' minlength='2' maxlength='2'  autocomplete='off' onKeyPress={this.isNumberKey} />
					<input type='text' name='year' id='yearSignup' pattern='[0-9]*' minlength='4' maxlength='4'  autocomplete='off' onKeyPress={this.isNumberKey} />
					<label for='gender' id='genderSignUpText' >Gender</label>
					<label id='femaleCheck' className='signupOption'>
					<input type='checkbox' for='gender' id='femaleCheck' name='sex' className='signupCheckBox' value='female'  /><span id='checkboxText'>FEMALE</span>
					</label>
					<label id='maleCheck' className='signupOption'>
					<input type='checkbox' for='gender' id='maleCheck' name='sex' className='signupCheckBox' value='male'  /><span id='checkboxText'>MALE</span>
					</label>
					<input name='gender' id='gender' hidden />
					<button id='signupButton'>REGISTER</button>


					</div> 
				</form>
			</div>
		)
	}

	isNumberKey(e){
		console.log('keyPress');
		console.log(e.target);
		// console.log(this.state);
	    var charCode = (e.which) ? e.which : e.keyCode
	    if (charCode > 31 && (charCode < 48 || charCode > 57)){
	        return false;
	    }
	    else{
	    	this.setState({month: e.key});
	    }
	}

	validEmail(v){
	    var r = new RegExp("[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?");
	    return (v.match(r) == null) ? false : true;
	}
}





export default SignUp;
