import React, { Component } from 'react';
import ScrollMenu from 'react-horizontal-scrolling-menu';
import Head from 'next/head';

import styled from 'styled-components';

import '../css/normalize.css';
import '../css/skeleton.css';
import '../css/style.css';
import '../css/shoppingCart.css';


class Cart extends React.Component{
	render(){
		console.log(this.props.data);

		if(this.props.data){

			return(
					<li className='cartList'>
						<img className='cartItemImage' src={this.props.data.image1} />
						<p className='cartItemName'>{this.props.data.name}</p>
						<p className='cartItemSize'>{this.props.data.size}</p>
						<p className='cartItemQuantity'>Qty:</p>
						<div className="custom-select">
						  <select className='select-selected' id='quantity' onChange={this.setQuantity}>
						    <option value="1">1</option>
						    <option value="2">2</option>
						    <option value="3">3</option>
						    <option value="4">4</option>
						    <option value="5">5</option>
						    <option value="6">6</option>
						    <option value="7">7</option>
						    <option value="8">8</option>
						    <option value="9">9</option>
						    <option value="10">10</option>
						  </select>
						</div>
						<p className='deleteItem' id='x' onClick={this.deleteItem}>&#10005;</p>
					</li>
			)
		}
			return(
					<li className='cartList'>
						<div className='empty'>Your shopping cart is empty</div>
					</li>
			)

	}

	setQuantity(){
		var items = document.querySelectorAll(".cartList");

		for (var k=0; k<items.length; k++) {
		    items[k].index = k;
		    var selected = items[k];
		    items[k].addEventListener("change", function () {
		        let id = this.index;
		        var myData = localStorage.getItem('state');
		        let data = JSON.parse(myData)
		        if(data.length == undefined || data.length === 1){
		        	console.log('one');
		        	data[0].quantity = selected.children[4].children[0].value;
		   //      	localStorage.clear();
					// localStorage.setItem('state',JSON.stringify(data));
					// location.reload();
					console.log(data);
		        }
		        else{
					for(var i = 0;i<data.length;i++) {
					    if(i == id) {
					    	console.log(i);
					        console.log('two or more');
					        data[i].quantity = selected.children[4].children[0].value;
					        // localStorage.clear();
					        // localStorage.setItem('state',JSON.stringify(data));
					        // location.reload();
					        console.log(data);
					        break;
					    }
					}
				}
		    });
		};

	}

	deleteItem(){

		var items = document.querySelectorAll(".cartList");

		for (var i=0; i<items.length; i++) {
		    items[i].index = i;
		    items[i].addEventListener("click", function () {
		        let id = this.index;
		        var myData = localStorage.getItem('state');
		        let data = JSON.parse(myData)
		        if(data.length == undefined || data.length === 1){
		        	localStorage.clear();
		        	location.reload();
		        }
		        else{
					for(var i = 0;i<data.length;i++) {
					    if(i == id) {
					        data.splice(i, 1);
					        localStorage.clear();
					        localStorage.setItem('state',JSON.stringify(data));
					        location.reload();
					        break;
					    }
					}
				}
		    });
		};

	}

}

export default Cart;
