import React from 'react';
import Head from 'next/head';

import styled from 'styled-components';

import Store from './store.js'
import '../scrollStore.js';

import '../css/normalize.css';
import '../css/skeleton.css';
import '../css/styleStore.css';


class StoreGridScroll extends React.Component {

	render () {
    	return(
    		<div className="pn-ProductNav_WrapperStore">
				<nav id="pnProductNav" className="pn-ProductNavStore">
				    <div id="pnProductNavContents" className="pn-ProductNav_Contentsstore">
				        {this.props.store.map((post, i) => <Store {...this.props} key={i} i={i} store={post} />)}
				    <span id="pnIndicator" className="pn-ProductNav_IndicatorStore"></span>
				    </div>
				</nav>
				    <button id="pnAdvancerLeft" className="pn-Advancer pn-Advancer_Left" type="button">
				    	<img className='leftArrow' src="https://i.imgur.com/uOKhqK5.png" title="source: imgur.com" />
				    </button>
				    <button id="pnAdvancerRight" className="pn-Advancer pn-Advancer_Right" type="button">
						<img className='rightArrow' src="https://i.imgur.com/lgw31BS.png" title="source: imgur.com" />
				    </button>
			</div>
    	)
 	 }

}

export default StoreGridScroll;
