import React from 'react';
import Head from 'next/head';
import Link from 'next/link';

import styled from 'styled-components';

import '../css/normalize.css';
import '../css/skeleton.css';
import '../css/styleStore.css';

class Store extends React.Component{
	constructor(props){

	  	super(props)

	    let data = props.artist.find(i => i.code === 'abcd');
	   	console.log(props)
		this.state = {
			code:this.props.store.code,
			name:this.props.store.name,
	    	display_src:this.props.store.display_src,
	    	price:this.props.store.price
		}

	}
	render(){

		return(
				<figure className='grid-figureStore'>
					<div className='grid-figure-wrapStore'>
						<Link href={`/store?code=${this.code}`}>
						<div alt={this.code} className='grid-store'>
							<p className='itemName'>{this.props.store.name}</p>
							<img src={this.props.store.display_src} alt={this.props.store.name} className='itemPhoto' />
							<p className='itemPrice'>${this.props.store.price}</p>
						</div>
						</Link>
					</div>
				</figure>
		)
	}
}

export default Store;
