import React from 'react';
import Head from 'next/head';

import styled from 'styled-components';

import Navbar3 from './navbar3.js';

import '../script.js';

import '../css/artist.css';
import '../css/normalize.css';
import '../css/skeleton.css';

class Items extends React.Component{


	constructor(props){

	    super(props)
	    let store = this.props.store;
	   	let itemNumber = this.props.code.code;

	    let result = store.filter(obj => {
  			return obj.code === itemNumber
		})

		result.map((e) => 
		   	this.state = {
				image1:e.display_src,
				name:e.name,
				price:e.price,
				description:e.description,
				color:e.color
			}
		);

	}

	state ={

	}

	render(){

		const check = function(el) {
		  var button = document.getElementById("button");

		  var nbr_checked_radios = document.querySelectorAll('input[type=radio]:checked').length;

		  console.log(nbr_checked_radios);
		  console.log(button);
		  /*
		     'nbr_of_checked_no>0' : mean if at least one of the 'no' options checked
		     'nbr_checked_radios==0' : mean if no radio button is checked 
		  */
		  if (nbr_checked_radios>0) {
		    button.disabled = false;
		  } 
		  else {
		    button.disabled = true;
		  }
		}


    const addToCart = function(){
    	let rate_value;
;    	if (document.getElementById('r1').checked) {
  			rate_value = document.getElementById('r1').value;
		}
        console.log('click');
    }

		return(
			<div className='always'>
				<Navbar3 name={this.state.name}/>
				<img className='itemMainPhoto' src={this.state.image1} />
				<p className='itemMainPrice'>${this.state.price}</p>
				<p className='itemMainDescription'>{this.state.description}</p>
					<div class="sizeSelect">

						<p>Size Select:</p>

						<ul className='sizeOptions'>
						  	<li>
							    <input type="radio" id="s-option" name="selector" onClick={check}/>
							    <label for="s-option">S</label>

							    <div class="check"></div>
						  	</li>

						  	<li>
							    <input type="radio" id="m-option" name="selector" onClick={check}/>
							    <label for="m-option">M</label>

							    <div class="check"><div class="inside"></div></div>
						  	</li>						  
						  	<li>
							    <input type="radio" id="l-option" name="selector" onClick={check}/>
							    <label for="l-option">L</label>

							    <div class="check"><div class="inside"></div></div>
						  	</li>
						  	<li>
							    <input type="radio" id="xl-option" name="selector" onClick={check}/>
							    <label for="xl-option">XL</label>

							    <div class="check"><div class="inside"></div></div>
						  	</li>
						</ul>
					</div>
				<p className='itemMainColor'>{this.state.color}</p>
				<button className='AddCart' id='button' onClick={addToCart}>Add to Cart</button>
			</div>
		)
	}

	componentDidMount(){
				if (process.browser){
			document.getElementById("button").disabled = true;
		}
	}
}

export default Items;
