import React from 'react';
import Head from 'next/head';

import styled from 'styled-components';

import Navbar3 from './navbar3.js';

import '../css/artist.css';
import '../css/normalize.css';
import '../css/skeleton.css';

class Items extends React.Component{


	constructor(props){

	    super(props)
	    let store = this.props.store;
	   	let itemNumber = this.props.code.code;

	    let result = store.filter(obj => {
  			return obj.code === itemNumber
		})

		result.map((e) => 
		   	this.state = {
				image1:e.display_src,
				name:e.name,
				price:e.price,
				description:e.description,
				color:e.color
			}
		);

	}

	state ={

	}

	render(){

		return(
			<div className='always'>
				<Navbar3 name={this.state.name}/>
				<img className='itemMainPhoto' src={this.state.image1} />
				<p className='itemMainPrice'>${this.state.price}</p>
				<p className='itemMainDescription'>{this.state.description}</p>
					<div class="sizeSelect">

						<p>Size Select:</p>

						<ul className='sizeOptions'>
						  	<li>
							    <input type="radio" id="s-option" name="selector" onclick='set_btn_status()' />
							    <label for="s-option">S</label>

							    <div class="check"></div>
						  	</li>

						  	<li>
							    <input type="radio" id="m-option" name="selector" onclick='set_btn_status()' />
							    <label for="m-option">M</label>

							    <div class="check"><div class="inside"></div></div>
						  	</li>						  
						  	<li>
							    <input type="radio" id="l-option" name="selector" onclick='set_btn_status()' />
							    <label for="l-option">L</label>

							    <div class="check"><div class="inside"></div></div>
						  	</li>
						  	<li>
							    <input type="radio" id="xl-option" name="selector" onclick='set_btn_status()' />
							    <label for="xl-option">XL</label>

							    <div class="check"><div class="inside"></div></div>
						  	</li>
						</ul>
					</div>
				<p className='itemMainColor'>{this.state.color}</p>
				<button className='AddCart' id='itemChoose' onclick='status()'>Add to Cart</button>
			</div>
		)
	}
}

export default Items;
