		    // console.log('steph');
		    // console.log(parsedData);
			this.state = {
				data:parsedData,
				total:'',
				shipping:9.99,
				tax:'',
				totalAmount:''
			}		
		}
		else{
			console.log('control');
			this.state = {
				data:'',
				total:'',
				shipping:9.99,
				tax:'',
				totalAmount:''
			}
		}
	}

	state={

	}

