import React from 'react';
import Head from 'next/head';

import styled from 'styled-components';

import Layout3 from '../components/layout3.js';

import store from '../data/store.js';

import '../css/normalize.css';
import '../css/skeleton.css';
import '../css/bootstrap.css';


// const StorePage = (props) => (
// 	<Layout3>
// 		<div>
// 		{console.log(props.url.query.code)}
// 		</div>
// 	</Layout3>
// );

class StorePage extends React.Component {

 	render () {
 		let itemNumber = this.props.url.query.code;

	    let result = store.filter(obj => {
  			return obj.code === itemNumber
		})

		let name = result.map((e) =>
			<p key={e.code}>{e.name}</p>
		)

		let image = result.map((e) =>
			<p key={e.code}>{e.display_src}</p>
		)

		let price = result.map((e) =>
			<p key={e.code}>{e.price}</p>
		)

    	return(
			<Layout3>
				<div>
					{name}
					{image}
					{price}
				</div>
			</Layout3>
    	)
 	 }

}

export default StorePage;



